function ub_fill_datalb(mybotDataSet){
	ub_bat_stats = mybotDataSet.data.orange_cap;
	ub_bowl_stats = mybotDataSet.data.purple_cap;
	ub_table_head = '<tr><th>Team</th><th>Matches</th><th>Win</th><th>Loss</th><th>Tie</th><th>NR</th><th>NRR</th><th>Points</th></tr>';
	ub_bt_table_body = '<tr><th>__UBBTNAME__</th><th>__UBBTTEAM__</th><th>__UBBTINN__</th><th>__UBBTRUNS__</th><th>__UBBTAVG__</th><th>__UBBTSR__</th></tr>';
	ub_bo_table_body = '<tr><th>__UBBONAME__</th><th>__UBBOTEAM__</th><th>__UBBOMATCHES__</th><th>__UBBOWICKETS__</th></tr>';

  ub_bt_table = document.getElementById('ub-bt-table');
  ub_bo_table = document.getElementById('ub-bo-table');
  z_bt = ub_bt_table.innerHTML;
  z_bo = ub_bo_table.innerHTML;
  for(var i=0;i<=2;i++){
		batteam = teamsLB[ub_bat_stats[i].team];
		bowlteam = teamsLB[ub_bowl_stats[i].team];
    y_bt = ub_bt_table_body.replace(/__UBBTNAME__/g, ub_bat_stats[i].player_name);
    y_bt = y_bt.replace(/__UBBTTEAM__/g, batteam);
		y_bt = y_bt.replace(/__UBBTRUNS__/g, ub_bat_stats[i].player_runs);
    // y_bt = y_bt.replace(/__UBBTINN__/g, ub_bat_stats[i].innings);
    // y_bt = y_bt.replace(/__UBBTAVG__/g, ub_bat_stats[i].avg);
    // y_bt = y_bt.replace(/__UBBTSR__/g, ub_bat_stats[i].strikeRate);
		y_bt = y_bt.replace(/__UBBTINN__/g, ub_bat_stats[i].match);
		y_bt = y_bt.replace(/__UBBTAVG__/g, ub_bat_stats[i].avg);
		y_bt = y_bt.replace(/__UBBTSR__/g, ub_bat_stats[i].sr);

    y_bo = ub_bo_table_body.replace(/__UBBONAME__/g, ub_bowl_stats[i].player_name);
    y_bo = y_bo.replace(/__UBBOTEAM__/g, bowlteam);
		y_bo = y_bo.replace(/__UBBOWICKETS__/g, ub_bowl_stats[i].player_wickets);
    // y_bo = y_bo.replace(/__UBBOMATCHES__/g, ub_bowl_stats[i].matches);
		y_bo = y_bo.replace(/__UBBOMATCHES__/g, ub_bowl_stats[i].match);

    z_bt += y_bt;
    z_bo += y_bo;
  }
  ub_bt_table.innerHTML = z_bt;
  ub_bo_table.innerHTML = z_bo;



}
